CREATE TABLE DOLGOZO(
DOLGOZO_ID NUMERIC(10) NOT NULL,
CEG_ID NUMERIC(10) NOT NULL,
NEV VARCHAR(64),
NEM CHAR(1),
SZULETESI_HELY VARCHAR(64),
SZULETESI_EV NUMERIC(4),
IGAZOLVANY_SZAM NUMERIC(10),
CONSTRAINT PK_DOLGOZO PRIMARY KEY(DOLGOZO_ID),
CONSTRAINT FK_CEG_ID FOREIGN KEY(CEG_ID) REFERENCES CEG(CEG_ID),
CONSTRAINT IG_SZAM_UNIQUE UNIQUE(IGAZOLVANY_SZAM)
)

CREATE TABLE BERLET(
BERLET_ID NUMERIC(10) NOT NULL,
MEGNEVEZES VARCHAR(64),
AR NUMERIC(10),
ERVENYESSEG_IDO NUMERIC(10),
KEDVEZMENY_TIPUS VARCHAR(64),
BERLET_FORMATUM VARCHAR(64),
CONSTRAINT PK_BERLET PRIMARY KEY(BERLET_ID),
CONSTRAINT AR_CHECK CHECK(AR>=0 OR 9999>=AR)
)

CREATE TABLE VASARLAS(
VASARLAS_ID NUMERIC(10) NOT NULL,
DOLGOZO_ID NUMERIC(10) NOT NULL,
BERLET_ID NUMERIC(10) NOT NULL,
BERLET_MEGNEVEZES VARCHAR(64),
IGAZOLVANY_SZAM NUMERIC(10),
ERVENYESSEG_KEZDETE DATE,
CONSTRAINT PK_VASARLAS PRIMARY KEY(VASARLAS_ID),
CONSTRAINT FK_DOLGOZO FOREIGN KEY(DOLGOZO_ID) REFERENCES DOLGOZO(DOLGOZO_ID),
CONSTRAINT FK_BERLET FOREIGN KEY(BERLET_ID) REFERENCES BERLET(BERLET_ID)
)

CREATE TABLE CEG(
CEG_ID NUMERIC(10) NOT NULL,
CEGNEV VARCHAR(64),
SZEKHELY VARCHAR(64),
ADOSZAM NUMERIC(10),
ALAPITAS_DATUMA DATE,
JEGYZETT_TOKE NUMERIC(10),
CONSTRAINT PK_CEG_ID PRIMARY KEY(CEG_ID)
)









